import React, {useEffect} from "react";
import { useSelector, useDispatch } from "react-redux";
import { setMealsValue } from "../../stores/Meals";
import axios from "axios";

const FoodOrder = () => {
  const meals = useSelector((state) => state.meals.value);
  const dispatch = useDispatch();

  const [orderItems, setOrderItems] = useState([]);

  useEffect(() => {
    const getMeals = async () => {
      try {
        const res = await axios.get("http://localhost:3001/meals");
        dispatch(setMealsValue(res.data));
      } catch (error) {
        console.log(error);
      }
    };

    getMeals();
  }, []);

  return (
    <section className="section-food-order">
        <div className="container">
            <h1 className="heading-primary">Yemək sifariş edin</h1>
            <form>
                {meals.map(meal => {
                    return (
                        <div className="meal-container">
                            <div className="meal-info">
                                <img src={meal.img} alt={meal.name} />
                                <div className="meal-info-text">
                                    <h3>{meal.name}</h3>
                                    <p className="meal-description">{meal.description}</p>
                                    <p className="meal-price">{meal.price} AZN</p>
                                </div>
                            </div>
                            <div className="meal-amount">
                                <input type="number" name={meal.name} onChange={handle} placeholder="0" />
                            </div>
                        </div>
                    )
                })}
            </form>
        </div>
    </section>
  );
};

export default FoodOrder;
